import React, { useState } from 'react';
import styles from './Header.module.css';

function Header() {
  // State to track hover effect
  const [isHovered, setIsHovered] = useState(false);

  return (
    <header className={styles.header}>
      <div className={styles.container}>
        <div className={styles.logo}>
          <img src="/images/logo.png" alt="Logo" width="60" />
        </div>
        
        {/* Links with hover effect */}
        <nav 
          className={styles.links}
          onMouseEnter={() => setIsHovered(true)}
          onMouseLeave={() => setIsHovered(false)}
        >
          {/* Icon (hamburger menu) */}
          <div className={styles.icon}>
            <span className={styles.iconLine}></span>
            {/* Adjust width of second span based on hover state */}
            <span 
              className={styles.iconLine}
              style={{ width: isHovered ? '100%' : '60%' }}
            ></span>
            <span className={styles.iconLine}></span>
          </div>
          
          {/* Dropdown menu list */}
          <ul className={styles.navList}>
            <li><a href="#home" className={styles.navItem}>Home</a></li>
            <li><a href="#features" className={styles.navItem}>Features</a></li>
            <li><a href="#services" className={styles.navItem}>Services</a></li>
            <li><a href="#portfolio" className={styles.navItem}>Portfolio</a></li>
            <li><a href="#about" className={styles.navItem}>About</a></li>
            <li><a href="#contact" className={styles.navItem}>Contact</a></li>
          </ul>
        </nav>
      </div>
    </header>
  );
}

export default Header;
